commands.statement:

INSERT INTO IdentityNo (Sequence, GroupID, CreatedMemberNo, CreatedAt) VALUES ((SELECT IFNULL(MAX(Sequence), 0) + 1 FROM IdentityNo WHERE GroupID = @GroupID)
    , @GroupID
    , @MemberNo
    , datetime('now'));

SELECT MAX(Sequence) AS IdentityNo FROM IdentityNo WHERE GroupID = @GroupID;


DELETE FROM IdentityNo WHERE No = @IdentityNo
    AND GroupID = @GroupID
    AND CreatedMemberNo = @MemberNo;

INSERT INTO Application (ApplicationNo , ApplicationID , ApplicationName , Acronyms , LogoPath , CompanyName , OwnerName , PublicYN , DeleteYN , Comment , CreatedMemberNo , CreatedAt ) VALUES (@ApplicationNo , @ApplicationID , @ApplicationName , @Acronyms , @LogoPath , @CompanyName , @OwnerName , @PublicYN , 'N' , @Comment , @MemberNo , DATETIME('now', 'localtime') );
INSERT INTO ApplicationMember (ApplicationNo , EmailID , MemberNo , MemberStatus , RoleDevelop , RoleBusiness , RoleOperation , RoleManaged , JoinAt , ExpiredAt , Options , CreatedMemberNo , CreatedAt ) VALUES (@ApplicationNo , (SELECT M.EmailID FROM [Member] M WHERE M.MemberNo = @MemberNo) , @MemberNo , 'J' , 'Y' , 'Y' , 'Y' , 'Y' , DATETIME('now', 'localtime') , '9999-12-31 23:59:59' , '' , @MemberNo , DATETIME('now', 'localtime') );
SELECT strftime('%Y-%m-%d %H:%M:%S', A.CreatedAt) AS CreatedAt FROM [Application] A WHERE A.ApplicationNo = @ApplicationNo;
