commands.statement:

INSERT OR REPLACE INTO RepositoryFile
(
    ItemID,
    RepositoryID,
    DependencyID,
    FileName,
    PhysicalPath,
    AbsolutePath,
    RelativePath,
    Extension,
    Size,
    MD5,
    CustomPath1,
    CustomPath2,
    CustomPath3,
    PolicyPath,
    SortingNo,
    Comment,
    CreatedMemberNo,
    CreatedAt
) 
VALUES
(
    @ItemID,
    @RepositoryID,
    @DependencyID,
    @FileName,
    @PhysicalPath,
    @AbsolutePath,
    @RelativePath,
    @Extension,
    @Size,
    @MD5,
    @CustomPath1,
    @CustomPath2,
    @CustomPath3,
    @PolicyPath,
    @SortingNo,
    @Comment,
    @CreatedMemberNo,
    DATETIME('now', 'localtime')
);
